mapper.select:
SELECT d.id, d.title, d.body, d.price, d.inserted, d.category, m.id memberId, m.nickName writerNickName FROM DesignBoard d LEFT JOIN Review r ON d.id = r.memberId JOIN Member m ON d.memberId = m.id
WHERE d.title LIKE #{keyword } OR d.body LIKE #{keyword }
WHERE d.title LIKE #{keyword }
WHERE d.body LIKE #{keyword }
WHERE m.nickName LIKE #{keyword }
GROUP BY d.id ORDER BY d.id DESC

mapper.update:
UPDATE DesignBoard
SET title = #{title},
	body = #{body},
	price = #{price}			
WHERE 
	id = #{id}

UPDATE DesignBoard
SET clicked = clicked + 1
WHERE id = #{id}

