mapper.select:
SELECT n.id id, n.title title, n.inserted inserted, n.managerId managerId FROM ManagerBoard n
WHERE n.title LIKE #{keyword } OR n.body LIKE #{keyword }
WHERE n.title LIKE #{keyword }
WHERE n.body LIKE #{keyword }
GROUP BY n.id ORDER BY id DESC

SELECT id, title, body, inserted
FROM ManagerBoard 
WHERE id = #{id}

SELECT id, title, body, inserted
FROM ManagerBoard
WHERE id = #{id}

mapper.update:
UPDATE ManagerBoard
SET title = #{title},
    body = #{body},
    managerId = #{managerId}
WHERE id = #{id}
